const ADD_NEWS = "ADD-NEWS";
const UPDATE_NEWS = "UPDATE-NEWS";

const newsReducer = (state, action) => {
  switch (action.type) {
    case ADD_NEWS:
      {
        let newNews = {
          header: state.newsChangeHead,
          text: state.newsChangeText,
        };
        state.news.unshift(newNews);

        state.newsChangeHead = "";
        state.newsChangeText = "";
      }
      return state;
    case UPDATE_NEWS:
      {
        state.newsChangeHead = action.newHeaderNews;
        state.newsChangeText = action.newTextNews;
      }
      return state;
    default:
      return state;
  }
};

export default newsReducer;
