import React from "react";
import { addNewsCreator, updateNewsCreator } from "../../redux/news_reducer";
import AdminNews from "./adminNews";

const AdminNewsContainer = (props) => {
  //   let state = props.store.getState();

  const addNews = () => {
    props.store.dispatch(addNewsCreator());
  };

  const postChange = (newsHeader, newsText) => {
    props.store.dispatch(updateNewsCreator(newsHeader, newsText));
  };

  return (
    <Consumer>
      {(store) => {
        return;
        <AdminNews
          updateNews={postChange}
          addNews={addNews}
          news={state.contain.advertising}
        ></AdminNews>;
      }}
    </Consumer>
  );
};

export default AdminNewsContainer;
